// ==================== 工具函数 ====================

// 获取所有平台
function getAllPlatforms() {
    return Object.values(PLATFORMS);
}

// 根据分类获取平台
function getPlatformsByCategory(categoryId) {
    return Object.values(PLATFORMS)
        .filter(p => {
            // 支持 category 为字符串或数组
            if (Array.isArray(p.category)) {
                return p.category.includes(categoryId);
            }
            return p.category === categoryId;
        })
        .sort((a, b) => a.order - b.order);
}

// 根据ID获取平台
function getPlatformById(platformId) {
    return PLATFORMS[platformId] || null;
}

// 获取平台统计信息
function getPlatformStats() {
    const categories = {};
    
    Object.values(PLATFORMS).forEach(platform => {
        if (!categories[platform.category]) {
            categories[platform.category] = 0;
        }
        categories[platform.category]++;
    });
    
    return {
        total: Object.keys(PLATFORMS).length,
        byCategory: categories
    };
}

